import React, { useState, useEffect } from 'react';
import { TextField, Button, Paper, Typography, Grid, Card, CardMedia, CardActionArea } from '@mui/material';
import axios from 'axios';

const ImageGenerator = () => {
  const [prompt, setPrompt] = useState('');
  const [images, setImages] = useState([]);
  const [storedImages, setStoredImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    fetchStoredImages();
  }, []);

  const fetchStoredImages = async () => {
    try {
      const response = await axios.get('/api/images/stored');
      setStoredImages(response.data);
    } catch (error) {
      console.error('Error fetching stored images:', error);
    }
  };

  const generateImage = async () => {
    setLoading(true);
    try {
      const response = await axios.post('/api/images/generate', { prompt });
      setImages(response.data.images.map((url, index) => ({
        url,
        localPath: response.data.local_paths[index]
      })));
      fetchStoredImages(); // Refresh stored images
    } catch (error) {
      console.error('Error generating image:', error);
    }
    setLoading(false);
  };

  const handleImageSelect = (image) => {
    setSelectedImage(image);
    // You can emit an event or call a parent component method here
    // to notify that an image has been selected
  };

  return (
    <Paper style={{ padding: '20px', maxWidth: '1200px', margin: '20px auto' }}>
      <Typography variant="h5" gutterBottom>
        Image Generator
      </Typography>
      <TextField
        fullWidth
        label="Describe the image you want to create"
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
        margin="normal"
        multiline
        rows={3}
      />
      <Button
        variant="contained"
        onClick={generateImage}
        disabled={loading}
        style={{ marginTop: '20px' }}
      >
        Generate Image
      </Button>

      {/* Recently Generated Images */}
      {images.length > 0 && (
        <>
          <Typography variant="h6" style={{ marginTop: '20px' }}>
            Recently Generated Images
          </Typography>
          <Grid container spacing={2} style={{ marginTop: '10px' }}>
            {images.map((image, index) => (
              <Grid item xs={12} sm={6} md={3} key={index}>
                <Card>
                  <CardActionArea onClick={() => handleImageSelect(image)}>
                    <CardMedia
                      component="img"
                      height="200"
                      image={image.url}
                      alt={`Generated ${index}`}
                      style={{ objectFit: 'cover' }}
                    />
                  </CardActionArea>
                </Card>
              </Grid>
            ))}
          </Grid>
        </>
      )}

      {/* Stored Images */}
      <Typography variant="h6" style={{ marginTop: '40px' }}>
        Stored Images
      </Typography>
      <Grid container spacing={2} style={{ marginTop: '10px' }}>
        {storedImages.map((image, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <Card>
              <CardActionArea onClick={() => handleImageSelect(image)}>
                <CardMedia
                  component="img"
                  height="200"
                  image={image.url}
                  alt={image.filename}
                  style={{ objectFit: 'cover' }}
                />
              </CardActionArea>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Paper>
  );
};

export default ImageGenerator;
