import React, { useState } from 'react';
import { 
  TextField, 
  Button, 
  Paper, 
  Typography, 
  Grid,
  CircularProgress,
  Box
} from '@mui/material';
import axios from 'axios';

const ImageGenerator = () => {
  const [prompt, setPrompt] = useState('');
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadedImageUrl, setUploadedImageUrl] = useState('');

  const generateImage = async () => {
    setLoading(true);
    try {
      const response = await axios.post('/api/images/generate', { prompt });
      setImages(response.data.images);
    } catch (error) {
      console.error('Error generating image:', error);
    }
    setLoading(false);
  };

  const handleFileSelect = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('Please select a file first');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    setLoading(true);
    try {
      const response = await axios.post('/api/upload/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setUploadedImageUrl(response.data.url);
    } catch (error) {
      console.error('Error uploading image:', error);
    }
    setLoading(false);
  };

  return (
    <Paper style={{ padding: '20px', maxWidth: '800px', margin: '20px auto' }}>
      <Typography variant="h5" gutterBottom>
        Image Generator
      </Typography>

      <Box mb={4}>
        <Typography variant="h6" gutterBottom>
          Upload Your Own Image
        </Typography>
        <input
          accept="image/*"
          style={{ display: 'none' }}
          id="raised-button-file"
          type="file"
          onChange={handleFileSelect}
        />
        <label htmlFor="raised-button-file">
          <Button variant="contained" component="span">
            Select Image
          </Button>
        </label>
        {selectedFile && (
          <Box mt={2}>
            <Typography variant="body2">
              Selected: {selectedFile.name}
            </Typography>
            <Button
              variant="contained"
              onClick={handleUpload}
              disabled={loading}
              style={{ marginTop: '10px' }}
            >
              Upload
            </Button>
          </Box>
        )}
        {uploadedImageUrl && (
          <Box mt={2}>
            <img 
              src={uploadedImageUrl} 
              alt="Uploaded" 
              style={{ maxWidth: '100%', maxHeight: '300px' }} 
            />
          </Box>
        )}
      </Box>

      <Typography variant="h6" gutterBottom>
        Or Generate an Image
      </Typography>
      <TextField
        fullWidth
        label="Describe the image you want to create"
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
        margin="normal"
        multiline
        rows={3}
      />
      <Button
        variant="contained"
        onClick={generateImage}
        disabled={loading}
        style={{ marginTop: '20px' }}
      >
        {loading ? <CircularProgress size={24} /> : 'Generate Image'}
      </Button>
      <Grid container spacing={2} style={{ marginTop: '20px' }}>
        {images.map((image, index) => (
          <Grid item xs={6} key={index}>
            <img src={image} alt={`Generated ${index}`} style={{ width: '100%' }} />
          </Grid>
        ))}
      </Grid>
    </Paper>
  );
};

export default ImageGenerator;
